import React, { useEffect, useState } from "react";
import firebase from "firebase";
import { Button } from "react-bootstrap";
import debounce from "debounce";

const Page = ({ location }) => {
    const [ data, setDate ] = useState("");

    const firebaseConfig = {
        apiKey: "AIzaSyCL3z399TlGU0Sfiw7khQT7ywCValJIDDM",
        authDomain: "freewrite-40098.firebaseapp.com",
        databaseURL: "https://freewrite-40098.firebaseio.com",
        projectId: "freewrite-40098",
        storageBucket: "freewrite-40098.appspot.com",
        messagingSenderId: "228525122017",
        appId: "1:228525122017:web:36026015fcba76da6232a0",
        measurementId: "G-VYG3XJZB2F"
    };
    if (!firebase.apps.length) {
        firebase.initializeApp(firebaseConfig);
    }
    const firestore = firebase.firestore()
    const collRef = firestore.collection("pages");

    const clickHandler = () => {
        let vals = document.querySelector("textarea").value
        let loc = location.pathname.slice(1);

        collRef.doc(loc).set({
            pageContent: vals
        })
        .then(() => {
            console.log("Data submitted!")
        })
        .catch(e => {
            console.log(e)
        })
    }

    useEffect(async () => {
        let loc = location.pathname.slice(1);
        console.log("loc", loc)
        console.log(collRef.doc(loc))

        try {
            const collection = collRef.doc(loc)
            const firestoreData = await collection.get();
            document.querySelector("textarea").value = firestoreData.data().pageContent;
        } catch(err) {
            throw new Error(err);
        }
    }, [])

    return (
        <div>
            <h2 className="pt-4">Welcome to {location.pathname}!</h2>
            <p className="mb-3">Your notes will automatically save as your type</p>
            <textarea onChange={() => debounce(clickHandler(), 1000)} className="border"></textarea>
            <Button onClick={clickHandler}>SUBMIT</Button>
        </div>
    )
}

export default Page
